void calcularEstadisticas(int totalCalificaciones) {
    int calificacion;
    int aprobados = 0, suspensos = 0, sumaCalificaciones = 0;

    for (int i = 1; i <= totalCalificaciones; i++) {
        // Solicitar al usuario que ingrese cada calificación
