        printf("El mayor número triangular que no supere %d es: %d\n", x, mayorTriangular);
    }

    return 0;
}

// Función para verificar si un número es triangular
int esTriangular(int num) {
    int suma = 0;
    int i = 1;

    while (suma < num) {
        suma += i;
        i++;
    }

    return (suma == num);
}

// Función para encontrar el mayor número triangular que no supere el número dado
int mayorTriangularMenorIgualA(int num) {
    int suma = 0;
    int i = 1;

    while (suma <= num) {
        suma += i;
        i++;
    }

    return suma - i + 1;
}
