```c
#include <stdio.h>

// Función para verificar si un número es perfecto
int esPerfecto(int numero) {
    int sumaDivisores = 0;

    for (int i = 1; i < numero; i++) {
        if (numero % i == 0) {
            sumaDivisores += i;
        }
    }

    return (sumaDivisores == numero);
}

